function App() {

  return (
    <div className="App">
      <MyContext.Provider value={{text}}>
      <h1>Desafio SL7</h1>

      <FirstComponent/>
      <SecondComponent />
      </MyContext.Provider>

    </div>
  );
}

export default App;
