import React, { useState } from 'react'
import './FirstComponent.css'


const FirstComponent = ({text}) => {
 

  return (
    
   <div>
    <h3>Input:</h3>
    <input type="text" onChange={(e) => setText(e.target.value)}/>
    <h3>Div:</h3>
    <div className='text'> {text} </div>
   </div>
  )
}

export default FirstComponent
